import React from 'react';
import PropTypes from 'prop-types';
import styles from './styles/MagazineItem.module.scss';
import { Link } from 'react-router-dom';

export const MagazineItem = ({ listLink, image, imageText, name, date, title, text }) => {
    return (
        <li className={styles['blue-magazine-item']}>
            <Link to={listLink} className={styles['blue-magazine-link']}>
                <div
                    className={styles['blue-magazine-thumbnail-area']}
                    style={{ backgroundImage: `url(${image})`}}
                >
                    <span className={styles['blind']}>{imageText}</span>
                </div>
                <div className={styles['blue-magazine-text-area']}>
                    <strong className={styles['blue-magazine-title']}>{title}</strong>
                    {
                        name && date ?
                            <ul className={styles['info-list']}>
                                <li className={styles['info-item']}>{name}</li>
                                <li className={styles['info-item']}>{date}</li>
                            </ul>
                            : ''
                    }
                    <p className={styles['blue-magazine-text']}>{text}</p>
                </div>
            </Link>
        </li>
    );
};

MagazineItem.propTypes = {
    listLink: PropTypes.string,
    image: PropTypes.string,
    imageText: PropTypes.string,
    title: PropTypes.string,
    text: PropTypes.string,
};
