import React from 'react';
import PropTypes from 'prop-types';
import styles from './styles/TrPopup.module.scss';
import {TrButton} from "./TrButton";

export const TrPopup = ({ title, closeButton, popupText, buttonLabel1, buttonLabel2, ...props }) => {
    return (
        <section className={`${styles['tr-popup']} ${ closeButton ? styles['close-button'] : styles['non-close-button']}`}>
            <div className={styles['tr-popup-inner']}>
                <div className={styles['tr-popup-title-container']}>
                    <h2 className={styles['tr-popup-title']}>
                        {title}
                    </h2>
                    {
                        closeButton &&
                        <button type="button" className={styles['tr-popup-close-button']}>
                            <span className={styles['blind']}>닫기 버튼</span>
                        </button>
                    }
                </div>
                <div className={styles['tr-popup-contents-container']}>
                    <p className={styles['tr-popup-text']}>
                        {popupText}
                    </p>
                    {
                        !closeButton &&
                        <div className={styles['tr-popup-button-container']}>
                            {buttonLabel1 && (
                                <TrButton
                                    type={buttonLabel1.type}
                                    primary={buttonLabel1.primary}
                                    size={buttonLabel1.size}
                                    label={buttonLabel1.label}
                                />
                            )}
                            {buttonLabel2 && (
                                <TrButton
                                    type={buttonLabel2.type}
                                    primary={buttonLabel2.primary}
                                    size={buttonLabel2.size}
                                    label={buttonLabel2.label}
                                />
                            )}

                        </div>
                    }
                </div>
            </div>
        </section>
    );
};

TrPopup.propTypes = {
    title: PropTypes.string,
    closeButton: PropTypes.bool,
    popupText: PropTypes.string,
    buttonLabel1: PropTypes.shape({
        type: PropTypes.oneOf(['button', 'submit', 'reset']).isRequired,
        primary: PropTypes.bool.isRequired,
        label: PropTypes.string.isRequired,
        size: PropTypes.oneOf(['small', 'medium', 'large']).isRequired,
    }),
    buttonLabel2: PropTypes.shape({
        type: PropTypes.oneOf(['button', 'submit', 'reset']).isRequired,
        primary: PropTypes.bool.isRequired,
        label: PropTypes.string.isRequired,
        size: PropTypes.oneOf(['small', 'medium', 'large']).isRequired,
    }),
};

TrPopup.defaultProps = {
    title: '제목입니다',
    closeButton: true,
    popupText: '내용입니다',
    buttonLabel1: { type: 'button', primary: true, size: 'medium', label: '버튼1' },
    buttonLabel2: { type: 'button', primary: false, size: 'medium', label: '버튼2' },
};
